function getData(){$.ajax({type:"GET",datatype:"application/json",url:"/users/searchUsers",success:function(a){console.log("Got you the Search GET",a),displayData(a)},error:function(a){console.log("No Search GET for you! ",a)},complete:function(){console.log("Search GET function done")}})}function displayData(a){$(".displayUsers").empty();for(var b=0;b<a.length;b++){var c="Username: "+a[b].username+"\n",d="Name: "+a[b].name.first+" "+a[b].name.last+"\n",e="Email: "+a[b].email+"\n",f="<button data-id="+a[b]._id+" class='removeData'>Delete</button>";$(".displayUsers").append("<div>"+c+d+e+f+"</div>")}}function deleteUser(a){$.ajax({type:"DELETE",datatype:"application/json",url:"/users/"+a,success:function(a){console.log("User was DELETED",a),getData()},error:function(a){console.log("No DELETE for you! ",a)},complete:function(){console.log("DELETE function done")}})}function logout(){$.ajax({type:"GET",datatype:"application/json",url:"/users/logout",success:function(a){console.log("Logged out"),location.reload()},error:function(a){console.log("No Logout for you! ",a)},complete:function(){console.log("Logout function done")}})}$(document).ready(function(){getData(),$(".logout").on("click",function(){logout()}),$(".displayUsers").on("click",".removeData",function(){var a=$(this).data("id");deleteUser(a)})});